begin
wwv_flow_imp_shared.create_plugin(
 p_id=>wwv_flow_imp.id(10602243121018615821)
,p_plugin_type=>'REGION TYPE'
,p_name=>'IMAGE.GALLERY.DE.NHASKO'
,p_display_name=>'Image gallery'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('REGION TYPE','IMAGE.GALLERY.DE.NHASKO'),'')
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'  function image_gallery_ajax (',
'      p_region              in apex_plugin.t_region,',
'      p_plugin              in apex_plugin.t_plugin',
'  ) return apex_plugin.t_region_ajax_result',
'  is',
'    l_sqldata1 clob;',
'    l_sqldata2 clob;',
'    l_start varchar2(100);',
'    l_min   number;',
'    l_max   number;',
'    l_count number;',
'    ',
'  begin',
'    l_start := lower(substrb(ltrim(p_region.source, '' ''||chr(10)||chr(13)),1,50));',
'    ',
'    l_min := apex_application.g_x01;',
'    l_max := apex_application.g_x02;',
'    ',
'    ',
'',
'    if substr(l_start,1,7) = ''declare'' or substr(l_start,1,5) = ''begin'' then',
'      l_sqldata1 := apex_plugin_util.get_plsql_function_result(p_region.source);',
'    else ',
'      l_sqldata1 := p_region.source;',
'    end if;',
'',
'    l_sqldata2 := ''select * from (select sl.*, rownum RN, (select count(*) from (''||l_sqldata1||'')) RCOUNT from (''||l_sqldata1||'' order by id) sl ) where RN between ''||l_min||'' and ''||l_max;',
'',
'    apex_util.json_from_sql(',
'      sqlq => l_sqldata2',
'    );',
'   return null;',
'  end image_gallery_ajax;',
'  ',
' function image_gallery_render (',
'      p_region              in apex_plugin.t_region,',
'      p_plugin              in apex_plugin.t_plugin,',
'      p_is_printer_friendly in boolean ',
'  ) return apex_plugin.t_region_render_result is ',
'    l_style            apex_application_page_regions.attribute_01%type := p_region.attribute_01;',
'    l_autoplay         apex_application_page_regions.attribute_02%type := p_region.attribute_02;',
'    l_animationspeed   apex_application_page_regions.attribute_03%type := p_region.attribute_03;',
'    l_slideshow        apex_application_page_regions.attribute_04%type := p_region.attribute_04;',
'',
'    l_escape_html      varchar2(10);',
'  begin',
'    ',
'    apex_css.add_file (',
'      p_name      => ''ImageGallery'',',
'      p_directory => p_plugin.file_prefix );',
'          ',
'    apex_css.add_file (',
'      p_name      => ''prettyPhoto'',',
'      p_directory => p_plugin.file_prefix );',
'      ',
'    apex_javascript.add_library (',
'      p_name      => ''jquery.prettyPhoto'',',
'      p_directory => p_plugin.file_prefix,',
'      p_version   => null ',
'    );',
'      ',
'    apex_javascript.add_library (',
'      p_name      => ''ImageGallery'',',
'      p_directory => p_plugin.file_prefix,',
'      p_version   => null ',
'    );',
'    ',
'    ',
'    sys.htp.p(',
'      ''<div id="''||apex_plugin_util.escape(p_region.static_id, true) ||''">',
'         <div class="parent">',
'           <div class = "arrowleftDiv">',
'             <span class="arrowleft" id="prev"><i class="fa fa-chevron-left fa-2x"></i></span>',
'           </div>',
'           <div class="thumbnailgallery">',
'             <ul id ="image-Gallery" class="t-Cards gallery showrooms clearfix"></ul>',
'             <div id="imagePagination"><span></span></div>',
'           </div>',
'           <div class="arrowrightDiv">',
'             <span class="arrowright" id="next"><i class="fa fa-chevron-right fa-2x"></i></span>',
'           </div>',
'         </div>'');',
'    sys.htp.p(''</div>'');',
'',
'    ',
'apex_javascript.add_onload_code (',
'      ''image_gallery(''|| ',
'        apex_javascript.add_value(apex_plugin_util.escape(p_region.static_id, true),   true)  ||',
'        apex_javascript.add_value(apex_plugin_util.escape(l_style, true),              true)  ||',
'        apex_javascript.add_value(apex_plugin_util.escape(l_autoplay, true),           true)  ||',
'        apex_javascript.add_value(apex_plugin_util.escape(l_animationspeed, true),     true)  ||',
'        apex_javascript.add_value(apex_plugin_util.escape(l_slideshow, true),          true)  ||',
'        apex_javascript.add_value(apex_plugin.get_ajax_identifier,                     false)  ||'');''',
'    );',
'    return null;',
'  end image_gallery_render; '))
,p_api_version=>2
,p_render_function=>'image_gallery_render'
,p_ajax_function=>'image_gallery_ajax'
,p_standard_attributes=>'SOURCE_LOCATION:NO_DATA_FOUND_MESSAGE:ORDER_BY'
